function A = Jacobiano(X)   u = X(1);  v = X(2);  p = X(3);  q = X(4);    u2 = u^2;  v2 = v^2;    A(1,1) = 0;  A(1,2) = 0;  A(1,3) = 1;  A(1,4) = 0;    A(2,1) = 0;  A(2,2) = 0;  A(2,3) = 0;  A(2,4) = 1;    A(3,1) = (4*u*v*p*q)/((u2 + v2 + 1)^2);  A(3,2) = ((-2*p*q)* (v2 +u2 +1))/ ((u2 + v2 + 1)^2);  A(3,3) = ((-2*v*q)/ (u2 + v2 + 1));  A(3,4) = ((-2*v*p)/ (u2 + v2 + 1));    A(4,1) = ((-2*p*q)* (u2 + v2 +1))/ ((u2 + v2 + 1)^2);  A(4,2) = (4*u*v*p*q)/((u2 + v2 + 1)^2);  A(4,3) = (-2*u*q)/ (u2 + v2 + 1);  A(4,4) = (-2*u*p)/ (u2 + v2 + 1);endfunction